DROP TABLE IF EXISTS tasks;
DROP TYPE IF EXISTS task_state;
DROP TYPE IF EXISTS task_priority;
DROP SEQUENCE IF EXISTS task_id_seq;

CREATE TYPE task_state AS ENUM('TODO','IN_PROGRESS','DONE');
CREATE TYPE task_priority AS ENUM('NONE','LOW','MEDIUM','HIGH');
CREATE SEQUENCE task_id_seq;

CREATE TABLE tasks(
      id         	INTEGER PRIMARY KEY DEFAULT(nextval('task_id_seq')),
      name       	VARCHAR(128) NOT NULL,
      state      	task_state DEFAULT('TODO'),
      starred     	BOOLEAN DEFAULT(false),
      description 	VARCHAR(5000) DEFAULT(''),
      priority    	task_priority DEFAULT('NONE'),
      due_date     	DATE,
      created_at   	DATE DEFAULT(now())
);

INSERT INTO tasks(name, state, starred, description, priority, due_date, created_at) VALUES ('Faire les courses', 'TODO', false, 'Acheter des fruits, du lait et du pain au supermarché du quartier. Ne pas oublier de vérifier la date de péremption des produits.', 'MEDIUM', NULL, '2024-04-01T01:43:04.136Z');
INSERT INTO tasks(name, state, starred, description, priority, due_date, created_at) VALUES ('Préparer la PRÉSENTATION', 'IN_PROGRESS', true, 'Terminer les diapositives pour la réunion de lundi prochain. Inclure des graphiques et des exemples concrets pour illustrer les points clés.', 'LOW', '2024-03-25', '2024-03-17T14:30:00.000Z');
INSERT INTO tasks(name, state, starred, description, priority, due_date, created_at) VALUES ('Faire du sport', 'IN_PROGRESS', true, 'Faire 30 minutes de course à pied dans le parc près de la maison. Profiter du beau temps pour s''aérer et se détendre.', 'HIGH', NULL, '2024-03-16T18:00:00.000Z');
INSERT INTO tasks(name, state, starred, description, priority, due_date, created_at) VALUES ('Appeler le service client', 'IN_PROGRESS', false, 'Résoudre un problème de facturation avec le service client de l''opérateur téléphonique. Préparer les informations de compte et les détails de la facture.', 'MEDIUM', '2024-03-20', '2024-03-19T10:45:00.000Z');
INSERT INTO tasks(name, state, starred, description, priority, due_date, created_at) VALUES ('Répondre aux e-mails', 'TODO', true, 'Répondre aux e-mails en attente dans la boîte de réception professionnelle. Classer les e-mails par importance et traiter les demandes urgentes en premier.', 'MEDIUM', NULL, '2024-03-18T16:20:00.000Z');
INSERT INTO tasks(name, state, starred, description, priority, due_date, created_at) VALUES ('Nettoyer la maison en commençant par le salon et en finissant par les toilettes', 'DONE', false, NULL, 'LOW', '2024-03-19', '2024-03-17T09:00:00.000Z');
