INSERT INTO tasks(name, state, starred, description, priority, due_date, created_at) VALUES ('Faire une pause', 'TODO', false, 'Prendre une courte pause de 15 minutes pour se détendre et recharger ses batteries. Faire une promenade à l''extérieur ou méditer pour se relaxer.', 'HIGH', NULL, '2024-03-19T13:00:00.000Z');
INSERT INTO tasks(name, state, starred, description, priority, due_date, created_at) VALUES ('Lire un livre', 'TODO', true, 'Lire le nouveau livre acheté la semaine dernière sur le développement personnel. Allouer du temps avant le coucher pour se plonger dans la lecture et apprendre de nouvelles choses.', 'MEDIUM', NULL, '2024-03-17T20:00:00.000Z');
INSERT INTO tasks(name, state, starred, description, priority, due_date, created_at) VALUES ('Faire de l''exercice', 'DONE', false, 'Faire une séance d''entraînement de 45 minutes à la salle de sport locale. Inclure des exercices de cardio et de musculation pour maintenir une bonne condition physique.', 'LOW', '2024-03-19', '2024-03-18T07:00:00.000Z');
INSERT INTO tasks(name, state, starred, description, priority, due_date, created_at) VALUES ('Acheter un cadeau d''anniversaire sur Amazon', 'IN_PROGRESS', true, 'Trouver un cadeau spécial pour l''anniversaire de maman. Explorer les boutiques locales pour des idées uniques.', 'NONE', NULL, '2024-03-20T09:00:00.000Z');
